</div>


<script>


    $(document).ready(function () {

        $(""#kendoGroupAnalysisGrid"").on(""dblclick"", ""tr.k-state-selected"", function (element) {

            $(""#kendoGroupAnalysisGrid"").find(""tr.k-state-selected td a.edit"").click();

        });

    })


    function ActiveDeactiveAnalysis(element) {

        let id = $(element).attr('data-id');
        $("".loader"").removeClass(""hidden"");
        $.ajax({
            type: ""Post"",
            url: ""/GroupAnalysis/ActiveDeactiveAnalysis"",
            data: { AnalysisId: id },
            success: function (response) {

                $("".loader"").fadeIn(""slow"");
                $("".loader"").addClass(""hidden"");
                $(""#kendoGroupAnalysisGrid"").find("".k-pager-refresh"").click();
            }
        });

    }


</script>");
