</div>

<script>


    $(document).ready(function () {

        $(""#kendoHumanSalaryGrid"").on(""dblclick"", ""tr.k-state-selected"", function (element) {

            $(""#kendoHumanSalaryGrid"").find(""tr.k-state-selected td a.edit"").click();

        });

    })

    function changeDateFormat(BeginDate) {
        if (BeginDate === null)
            return """";
        var year = BeginDate.getFullYear();
        var month = BeginDate.getMonth();
        var day = BeginDate.getDate();
        var d = day + ""-"" + month + ""-"" + year;
        return d;
    }

    function LineItems_Databound(status) {
        if (status == 'Paid') {
            return ""<div style='background:lightgreen;border-radius: 5px;'>"" + status + "" </div>"";
        } else {
            return ""<div style='background:pink;border-radius: 5px;'>"" + status + "" </div>"";
        }
    }

</script>");
